import React, { useEffect, useState } from 'react';
import { getData } from '../utils/data';
import { getLocalStorage, setLocalStorage } from '../utils/localStorage';

export default function Appetizers() {
  const ENDPOINT = 'Appetizers';
  const [appetizers, setAppetizers] = useState([]);
  
  useEffect(() => {
    let data = getLocalStorage(ENDPOINT);
    if (data.length > 0) {
      setAppetizers(data);
    } else {
      getData(ENDPOINT)
        .then((data) => {
          setAppetizers(data);
          setLocalStorage(ENDPOINT, data);
        })
    }
  }, []);

  return (
    <main style={{ padding: "1rem 0" }} className="container">
      <div className="row text-center justify-content-center gap-2">
        <h2>Appetizers</h2>
        {appetizers.map((appetizers) => <Appetizers key={appetizers.id} appetizers={appetizers} />)}
      </div>
    </main>
  );
}

const Appetizer = ({ Appetizers }) => {
    return (
      <div className='card col-3'>
        <div className="card-body">
          {Appetizers.name && <h2 className='card-title'>{Appetizers.name}</h2>}
          <div><strong>Effect:</strong> {Appetizers.effect}</div>
          <div><strong>Side Effects:</strong> {Appetizers.sideEffects}</div>
          <div><strong>Characteristics:</strong> {Appetizers.characteristics}</div>
          <div><strong>Ingredients:</strong> {Appetizers.ingredients.map((ingredient) => ingredient.name).join(', ')}</div>
          <div><strong>Time:</strong> {Appetizers.time}</div>
          <div><strong>Difficulty:</strong> {Appetizers.difficulty}</div>
          <div><strong>Manufacturer:</strong> {Appetizers.manufacturer}</div>
        </div>
      </div>
    )
  }
